import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_WEATHER_REQUEST } from '../apollo/queries';
import AnimatedBackground from '../components/AnimatedBackground';
import SearchBar from '../components/SearchBar';
import WeatherDashboard from '../components/WeatherDashboard';
import AirQualityPanel from '../components/AirQualityPanel';
import MapComponent from '../components/MapComponent';
import VideoGallery from '../components/VideoGallery';
import CityInfoPanel from '../components/CityInfoPanel';
import LoadingScreen from '../components/LoadingScreen';
import { Download, Mail, Info } from 'lucide-react';
import { motion } from 'framer-motion';

const Home = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [createWeatherRequest, { loading }] = useMutation(CREATE_WEATHER_REQUEST);

  const handleSearch = async (formData) => {
    try {
      const { data } = await createWeatherRequest({
        variables: {
          input: {
            location_query: formData.location,
            start_date: formData.startDate,
            end_date: formData.endDate,
            user_email: formData.email || undefined
          }
        }
      });

      if (data && data.createWeatherRequest) {
        setWeatherData(data.createWeatherRequest);
      }
    } catch (error) {
      console.error('Error fetching weather:', error);
      alert(`Error: ${error.message}`);
    }
  };

  const handleExport = (format) => {
    const baseUrl = process.env.REACT_APP_API_URL || 'http://localhost:5001';

    // If we have weather data, export it directly
    if (weatherData) {
      const exportUrl = `${baseUrl}/api/export/single?format=${format}&id=${weatherData.id}`;
      window.open(exportUrl, '_blank');
    } else {
      // Export all data
      window.open(`${baseUrl}/api/export?format=${format}`, '_blank');
    }
  };

  return (
    <div className="min-h-screen relative">
      <AnimatedBackground />

      <div className="relative z-10 container mx-auto px-4 py-8">
        {/* Header */}
        <motion.header
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mb-8 text-center"
        >
          <h1 className="text-6xl font-extralight tracking-wider mb-2 bg-clip-text text-transparent bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400">
            Weather Intelligence System
          </h1>
          <p className="text-white/60 text-lg">
            Advanced weather analytics powered by AI ‚Ä¢ Built by{' '}
            <span className="text-blue-400 font-medium">Rajeev Ranjan Pratap Singh</span>
          </p>
          <div className="mt-4">
            <a
              href="https://pmaccelerator.com"
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-2 text-sm text-white/70 hover:text-white transition-colors"
            >
              <Info className="w-4 h-4" />
              PM Accelerator Tech Assessment 2
            </a>
          </div>
        </motion.header>

        {/* Search Bar */}
        <SearchBar onSearch={handleSearch} loading={loading} />

        {/* Export Buttons */}
        {weatherData && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="flex flex-wrap gap-3 justify-center mb-6"
          >
            {['json', 'csv', 'xml', 'pdf', 'md'].map((format) => (
              <button
                key={format}
                onClick={() => handleExport(format)}
                className="btn-secondary flex items-center gap-2"
              >
                <Download className="w-4 h-4" />
                Export {format.toUpperCase()}
              </button>
            ))}
          </motion.div>
        )}

        {/* Loading Screen */}
        {loading && <LoadingScreen />}

        {/* Weather Data Display */}
        {weatherData && !loading && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="space-y-8"
          >
            {/* Main Weather Dashboard */}
            <WeatherDashboard weatherData={weatherData} />

            {/* Two Column Layout */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Left Column */}
              <div className="space-y-6">
                <AirQualityPanel airQuality={weatherData.external_metadata?.air_quality} />
                <MapComponent
                  location={weatherData.resolved_location}
                  pointsOfInterest={weatherData.external_metadata?.points_of_interest}
                />
              </div>

              {/* Right Column */}
              <div className="space-y-6">
                <CityInfoPanel cityData={weatherData.external_metadata} />
              </div>
            </div>

            {/* Full Width Video Gallery */}
            {weatherData.external_metadata?.youtube_videos && (
              <VideoGallery videos={weatherData.external_metadata.youtube_videos} />
            )}

            {/* Email Notification Status */}
            {weatherData.email_sent && weatherData.user_email && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="glass-card p-4 flex items-center gap-3 justify-center"
              >
                <Mail className="w-5 h-5 text-green-400" />
                <span className="text-white/80">
                  Weather report sent to <strong>{weatherData.user_email}</strong>
                </span>
              </motion.div>
            )}
          </motion.div>
        )}

        {/* Empty State */}
        {!weatherData && !loading && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center py-20"
          >
            <div className="text-6xl mb-6">üåç</div>
            <h2 className="text-3xl font-light mb-3">
              Discover Weather Intelligence
            </h2>
            <p className="text-white/60 max-w-2xl mx-auto">
              Enter any location to get comprehensive weather data, air quality analysis,
              city information, local events, and much more. Experience the future of
              weather forecasting.
            </p>
          </motion.div>
        )}
      </div>

      {/* Footer */}
      <footer className="relative z-10 mt-20 pb-8 text-center text-white/40 text-sm">
        <p>
          ¬© 2025 Weather Intelligence System ‚Ä¢ Developed by Rajeev Ranjan Pratap Singh
        </p>
      </footer>
    </div>
  );
};

export default Home;
