import React from 'react';
import { motion } from 'framer-motion';

const LoadingScreen = () => {
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-space-900/95 backdrop-blur-md">
      <div className="text-center">
        {/* Animated Weather Icon */}
        <motion.div
          className="relative w-32 h-32 mx-auto mb-8"
          animate={{
            rotate: 360,
          }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "linear"
          }}
        >
          <div className="absolute inset-0 rounded-full bg-gradient-to-r from-blue-500 to-purple-500 opacity-20 blur-xl"></div>
          <motion.div
            className="absolute inset-4 rounded-full bg-gradient-to-r from-cyan-400 to-blue-500"
            animate={{
              scale: [1, 1.2, 1],
              opacity: [0.5, 0.8, 0.5],
            }}
            transition={{
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          ></motion.div>
          <div className="absolute inset-0 flex items-center justify-center text-4xl">
            üå§Ô∏è
          </div>
        </motion.div>

        {/* Loading Text */}
        <motion.h2
          className="text-2xl font-light text-white mb-2"
          animate={{ opacity: [0.5, 1, 0.5] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          Fetching Weather Intelligence
        </motion.h2>

        {/* Loading Steps */}
        <div className="space-y-2 mt-6">
          {[
            'Resolving location...',
            'Gathering weather data...',
            'Analyzing air quality...',
            'Loading city information...',
          ].map((text, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: [0, 1, 0.5], x: 0 }}
              transition={{
                delay: index * 0.5,
                duration: 1.5,
                repeat: Infinity,
                repeatDelay: 1
              }}
              className="text-sm text-white/60"
            >
              {text}
            </motion.div>
          ))}
        </div>

        {/* Shimmer Bar */}
        <div className="mt-8 w-64 h-1 mx-auto bg-white/10 rounded-full overflow-hidden">
          <motion.div
            className="h-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"
            animate={{
              x: ['-100%', '200%'],
            }}
            transition={{
              duration: 1.5,
              repeat: Infinity,
              ease: "linear"
            }}
          ></motion.div>
        </div>
      </div>
    </div>
  );
};

export default LoadingScreen;
