import React, { useEffect, useRef } from 'react';

const AnimatedBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Create stars
    const stars = [];
    const starCount = 200;

    for (let i = 0; i < starCount; i++) {
      stars.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        radius: Math.random() * 1.5,
        alpha: Math.random(),
        deltaAlpha: Math.random() * 0.02 + 0.005
      });
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw stars
      stars.forEach(star => {
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.radius, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${star.alpha})`;
        ctx.fill();

        // Twinkling effect
        star.alpha += star.deltaAlpha;
        if (star.alpha <= 0 || star.alpha >= 1) {
          star.deltaAlpha = -star.deltaAlpha;
        }
      });

      requestAnimationFrame(animate);
    }

    animate();

    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <>
      <canvas
        ref={canvasRef}
        className="fixed inset-0 pointer-events-none z-0"
        style={{ background: 'transparent' }}
      />

      {/* Nebula blobs */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none z-0">
        <div className="nebula-blob w-96 h-96 bg-blue-500 top-[-10%] left-[-5%]" 
             style={{ animationDelay: '0s' }} />
        <div className="nebula-blob w-80 h-80 bg-purple-500 top-[20%] right-[-10%]" 
             style={{ animationDelay: '3s' }} />
        <div className="nebula-blob w-72 h-72 bg-indigo-500 bottom-[-5%] left-[30%]" 
             style={{ animationDelay: '6s' }} />
        <div className="nebula-blob w-64 h-64 bg-pink-500 bottom-[20%] right-[20%]" 
             style={{ animationDelay: '9s' }} />
      </div>
    </>
  );
};

export default AnimatedBackground;
